// Minimal PDF.js worker stub to allow the application to function
// This is a simplified version that will allow the fake worker mode to be used
// when the real worker cannot be loaded

(function () {
  'use strict';

  // Self in worker context refers to the worker global scope
  const workerGlobal = self;

  // Set up the worker global scope
  workerGlobal.pdfjsWorker = {};

  // Handle messages from the main thread
  workerGlobal.onmessage = function (event) {
    // Acknowledge receipt of the message
    workerGlobal.postMessage({
      type: 'ready',
      messageId: event.data && event.data.messageId
    });
  };

  // Signal that the worker is loaded
  workerGlobal.postMessage({
    type: 'workerReady'
  });

  console.log('PDF.js worker stub initialized');
})();
